targets.expr:
sum(
  argocd_app_info{
namespace=~'$namespace',
dest_server=~'$cluster',

    health_status!~"Healthy|Progressing"
  }
) by (job, dest_server, project, name, health_status)

sum(
  argocd_app_info{
namespace=~'$namespace',
dest_server=~'$cluster',

    sync_status!="Synced"
  }
) by (job, dest_server, project, name, sync_status) > 0
